async function main() {
  // Add fields parameter to api query string in order to
  // restrict the fields returned in the response.
  const recipe = await fetch(
    "https://dev-ds-demo.pantheonsite.io/en/jsonapi/node/recipe/33386d32-a87c-44b9-b66b-3dd0bfc38dca?fields[node--recipe]=title,field_difficulty,field_recipe_instruction,field_recipe_category&fields[taxonomy_term--recipe_category]=name&include=field_recipe_category"
  )
    .then((response) => response.json())
    .then((data) => data)
    .catch((error) => console.error("API fetch failed", error));

  document.getElementById(
    "app"
  ).innerHTML = `See console log for additional debug info.
<pre>
Recipe:
${JSON.stringify(recipe, null, 2)}
</pre>`;
}

main();
